import React, { useState, useEffect } from 'react';
import { Paper, makeStyles, Typography, Grid, RadioGroup, Radio, FormControl, FormControlLabel, Checkbox, Slider} from '@material-ui/core';


import styles from "../styles/QuestionStyle";

const useStyles = makeStyles(styles);

export default function Question(props) {

  const classes = useStyles();
  const [value, setValue] = useState("");
  const [radioValue, setRadioValue] = useState("");
  const [checked, setChecked] = useState(() => {
    if (props.qInfo.type==="cb") {
      let cb = [];
      for(let i=0; i<props.qInfo.options.length; i++) {
        cb = [...cb, false];
      }
      return cb;
    }
  });
  
  // console.log(value);
  // console.log(props.qInfo);
  
  useEffect(() => {

  }, []);

  const handleRadioChange = (e) => {
    setRadioValue(e.target.value);
    props.updateOptAnswer(props.index, Number(e.target.value));
  }

  const handleCbChange = (e) => {
    let cb = checked.slice();
    cb[e.target.value] = !cb[e.target.value];
    setChecked(cb);
    let ans = "";
    for (let i=0; i<cb.length; i++)
      if (cb[i]==true) 
        ans = ans + "&" + props.qInfo.options[i].content;
    console.log(ans);
    props.update(props.index, ans);
  }

  const handleSliderChange = (e, value) => {
    console.log(value);
    props.update(props.index, ans);
  }

  const displayMainArea = () => {
    if (props.qInfo.type==="mc") {
      return (
        <FormControl component="fieldset" >
        <RadioGroup onChange={handleRadioChange} value={radioValue.toString()}>
          {
            props.qInfo.options.map((opt, index) => (
              <FormControlLabel 
              value={opt.optId.toString()} 
              control={<Radio color="primary" />} 
              label={opt.content}
              key={index} />
            ))
          }
        </RadioGroup>
        </FormControl>
      );
    }
    else if (props.qInfo.type==="cb") {
      return (
        <FormControl>
          {
            props.qInfo.options.map((opt, index) => {
              return (
              <FormControlLabel 
              control={<Checkbox checked={checked[index]} onChange={handleCbChange} color="primary" />}
              label={opt.content} 
              value={index}
              key={index} />);
            })
          }
        </FormControl>
      );
    }
    else if (props.qInfo.type==="scale") {
      return (
        <Slider 
        onChange={handleSliderChange}
        defaultValue={3}
        min={0}
        max={5}
        step={1}
        marks 
        valueLabelDisplay="auto"
        style={{width: "40vw"}} />
      );
    }
  }

  return (
    <React.Fragment>
      <Paper className={classes.questionPaper}>
        <Grid container direction="column" alignItems="flex-start" >
          <Grid item>
            <Typography> {props.qInfo.content} </Typography>
          </Grid>
          <Grid item>
            {displayMainArea()}
          </Grid>
        </Grid>
      </Paper>
    </React.Fragment>
  );

}
