import React, { useState } from "react";
import { makeStyles, Paper, Grid, Select, MenuItem, TextField, IconButton } from "@material-ui/core";
import DeleteIcon from '@material-ui/icons/Delete';

import styles from "../styles/QuestionStyle";
import McQuestion from "./McQuestion";
import CbQuestion from "./CbQuestion";
import TextQuestion from "./TextQuestion";
import NumQuestion from "./NumQuestion";
import ScaleQuestion from "./ScaleQuestion";



const useStyles = makeStyles(styles);


export default function Question(props) {

  const classes = useStyles();
  const [info, setInfo] = useState(props.qInfo);
  const [selectorOpen, setSelectorOpen] = useState(false);

  //console.log(info);

  const handleClose = () => {
    setSelectorOpen(false);
  };

  const handleOpen = () => {
    setSelectorOpen(true);
  };

  const handleTypeChange = e => {
    let newInfo = info;
    newInfo['type'] = e.target.value;
    setInfo(newInfo);
    props.update();
  }

  const handleContChange = e => {
    let newInfo = info;
    newInfo['content'] = e.target.value;
    setInfo(newInfo);
    props.update();
  }

  const handleOptChange = options => {
    let newInfo = info;
    newInfo['options'] = options;
    setInfo(newInfo);
    props.update(props.index, info);
    // console.log(info['options']);
  }

  let question;
  if (info.type==="mc") question=<McQuestion qInfo={info} updateOpt={handleOptChange}/>;
  else if (info.type==="cb")  question=<CbQuestion qInfo={info} updateOpt={handleOptChange}/>;
  else if (info.type==="text")  question=<TextQuestion qInfo={info} />;
  else if (info.type==="num")   question=<NumQuestion qInfo={info} />;
  else if (info.type==="scale") question=<ScaleQuestion qInfo={info} />;

  return (
    <React.Fragment>
      <Paper className={classes.questionPaper}>
        <Grid container direction="column">
          <Grid item container direction="row" alignItems="flex-start">
            <Grid item className={classes.mainArea}>
                <TextField
                label={"question "+(props.index+1)}
                variant="filled"
                onBlur={handleContChange}
                className={classes.contentInput} >
                </TextField>
            </Grid>
            <Grid item >
              <Select
              className={classes.typeSelector}
              open={selectorOpen}
              onClose={handleClose}
              onOpen={handleOpen}
              value={info.type}
              onChange={handleTypeChange}>
              <MenuItem value={"mc"}>Multiple Choice</MenuItem>
              <MenuItem value={"cb"}>Checkboxes</MenuItem>
              <MenuItem value={"text"}>Text</MenuItem>
              <MenuItem value={"num"}>Number</MenuItem>
              <MenuItem value={"scale"}>Linear Scale</MenuItem>
              </Select>
            </Grid>
          </Grid>
          <Grid item>
            {question}
          </Grid>
          <Grid item container direction="row-reverse">
            <Grid item>
              <IconButton>
                <DeleteIcon />
              </IconButton>
            </Grid>
          </Grid>
        </Grid>
        
      </Paper>
      
        
    </React.Fragment>
  )

}
