import React, { useState, useEffect } from 'react';

import Question from "../components/Question";
import { Grid, Typography, Button, Paper } from '@material-ui/core';

export default function Survey(props) {

  const [cascade, setCascade] = useState([]); //记录被级联的问题id，在表里的不显示
  const [oldOptions, setOldOptions] = useState([]);//保存选项改变时的前一个值，从而恢复前一个值引发的级联状态变化
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [title, setTitle] = useState("");

  useEffect(() => {
    let headers = new Headers();
    headers.append("Content-Type", "application/json;charset=UTF-8");
    const init = {
      method: 'GET',
      headers: headers
    };
    //fetch 获取 title
    fetch(`/api/title/${props.match.params.survid}`, init)
      .then(res => res.json())
      .then(data => setTitle(data[0]));
    //fetch 获取 questions
    fetch(`/api/questions/${props.match.params.survid}`, init)
      .then(res => res.json())
      .then(data => setQuestions(data));
    //set state of cascade

    // //test: setCascade
    // setCascade([3, 3, 7, 8, 10, 3, 6, 4, 9]);
    // //test: setQuestions
    // setQuestions([
    //   {
    //     formId: 8,
    //     qstnId: 1,
    //     type: "mc",
    //     content: "question 1: multiple choices",
    //     options: [
    //       {
    //         optId: 1,
    //         content: "option 1",
    //         cascadeQstnId: 3
    //       },
    //       {
    //         optId: 2,
    //         content: "option 2",
    //         cascadeQstnId: 4
    //       }
    //     ]
    //   },
    //   {
    //     formId: 8,
    //     qstnId: 2,
    //     type: "realNum",
    //     content: "question 2: real number",
    //     options: []
    //   },
    //   {
    //     formId: 8,
    //     qstnId: 3,
    //     type: "scale",
    //     content: "question 3: scale",
    //     options: []
    //   },
    //   {
    //     formId: 8,
    //     qstnId: 4,
    //     type: "cb",
    //     content: "question 4: checkboxes",
    //     options: [
    //       {
    //         optId: 1,
    //         content: "option 1",
    //         cascadeQstnId: undefined
    //       },
    //       {
    //         optId: 2,
    //         content: "option 2",
    //         cascadeQstnId: undefined
    //       },
    //       {
    //         optId: 3,
    //         content: "option 3",
    //         cascadeQstnId: undefined
    //       }
    //     ]
    //   },
    //   {
    //     formId: 8,
    //     qstnId: 5,
    //     type: "text",
    //     content: "question 5: text",
    //     options: []
    //   },
    // ]);
    // //test: setTitle
    // setTitle("SURVEY 1");

  }, []);

  /**
   * front end test
   */
  console.log(answers);
  console.log(cascade);
  

  const isCascaded = qstnId => {
    for (let i=0; i<cascade.length; i++)
      if (cascade[i]===qstnId) return true;
    return false;
  }

  const updateOptAnswer = (index, optId) => {

    const ans = {
      resp_id: 0,
      form_id: Number(props.match.params.survid),
      qstn_id: questions[index].qstnId,
      content: questions[index].options[optId-1].content
    };
    let tempAns = answers.slice();
    tempAns[index] = ans;
    setAnswers(tempAns);
    //test
    console.log(answers);

    //旧选项如果有级联，则改变后将级联id加回级联状态表
    let cList = cascade.slice();
    if (oldOptions[index]) {
      let cId = questions[index].options[oldOptions[index]-1].cascadeQstnId;
      if (cId) {
        cList.unshift(cId);
        setCascade(cList);
      }
    }

    //更新旧选项表
    let old = oldOptions.slice();
    old[index] = optId;
    setOldOptions(old);

    //新的当前选项如果有级联，则在级联状态表删除掉所有该id
    let cId = questions[index].options[optId-1].cascadeQstnId;
    if (cId) {   
      for (let i=0; i<cList.length; i++) {
        if (cList[i] == cId) {
          cList.splice(i, 1);
          --i;
        }
      }
      setCascade(cList);
      console.log(cList);
    }
    console.log(oldOptions);
    console.log(cascade);
  }

  const updateAnswer = (index, ansCont) => {
    const ans = {
      resp_id: 0,
      form_id: Number(props.match.params.survid),
      qstn_id: questions[index].qstnId,
      content: ansCont
    };
    let tempAns = answers.slice();
    tempAns[index] = ans;
    setAnswers(tempAns);
    console.log(answers);
  }

  const submitResponse = () => {}

  return (
    <React.Fragment>
      <Grid container direction="column" alignItems="center" >
        <Grid item>
          <Paper>
            <Typography>
              {title}
            </Typography>
          </Paper>
        </Grid>
        {
          questions.map((q, index) => {
            if (isCascaded(q.qstnId)) return null;
            return (
              <Grid item key={index}>
                <Question 
                index={index}
                qInfo = {q}
                updateOptAnswer = {updateOptAnswer}
                update = {updateAnswer} />
              </Grid>
            );
          })
        }
        <Grid item>
          <Button
          variant = "contained"
          color = "primary"
          onClick = {submitResponse} >
            Submit
          </Button>
        </Grid>
      </Grid>
    </React.Fragment>
  );

}
