import React, { useState, useEffect } from 'react';
import { Paper, makeStyles, Typography, Grid, RadioGroup, Radio, FormControl, FormControlLabel, Checkbox} from '@material-ui/core';


import styles from "../styles/QuestionStyle";

const useStyles = makeStyles(styles);

export default function Question(props) {

  const classes = useStyles();
  const [radioValue, setRadioValue] = useState('');
  const [checked, setChecked] = useState([]);
  
  // console.log(value);
  // console.log(props.qInfo);
  
  useEffect(() => {
    if (props.qInfo.type==="cb") {
      let cb = [];
      for(let i=0; i<props.qInfo.options.length; i++) {
        cb = [...cb, false];
      }
      setChecked(cb);
    }
  }, []);

  const handleRadioChange = (e) => {
    setRadioValue(e.target.value);
    props.update(props.index, Number(e.target.value));
  }

  const handleCbChange = (e) => {
    console.log(e.target.value);
    let cb = checked.slice();
    cb[e.target.value] = !cb[e.target.value];
    setChecked(cb);
    console.log(cb);
  }

  const displayMainArea = () => {
    if (props.qInfo.type==="mc") {
      return (
        <FormControl component="fieldset" >
        <RadioGroup onChange={handleRadioChange} value={radioValue.toString()}>
          {
            props.qInfo.options.map((opt, index) => (
              <FormControlLabel 
              value={opt.optId.toString()} 
              control={<Radio color="primary" />} 
              label={opt.content}
              key={index} />
            ))
          }
        </RadioGroup>
        </FormControl>
      );
    }
    else if (props.qInfo.type==="cb") {
      return (
        <FormControl>
          {
            props.qInfo.options.map((opt, index) => {
              return (
              <FormControlLabel 
              control={<Checkbox checked={checked[index]} onChange={handleCbChange} color="primary" />}
              label={opt.content} 
              value={index}
              key={index} />);
            })
          }
        </FormControl>
      );
    }
  }

  return (
    <React.Fragment>
      <Paper>
        <Grid container direction="column" alignItems="flex-start" >
          <Grid item>
            <Typography> {props.qInfo.content} </Typography>
          </Grid>
          <Grid item>
            {displayMainArea()}
          </Grid>
        </Grid>
      </Paper>
    </React.Fragment>
  );

}
