import React, { useState, useEffect } from "react";
import { makeStyles, Grid, TextField, Button, IconButton } from "@material-ui/core";
import RadioButtonUncheckedIcon from '@material-ui/icons/RadioButtonUnchecked';
import ClearIcon from '@material-ui/icons/Clear';

import styles from "../styles/QuestionStyle";


const useStyles = makeStyles(styles);


function Option(props) {

  const classes = useStyles();

  const [opt, setOpt] = useState(props.option);


  useEffect(() => {
    setOpt(props.option);
  })

  const deleteItself = e => {
    props.deleteOption(props.index, e);
  }

  const handleNameChange = e => {
    let newOpt = opt;
    newOpt['name'] = e.target.value;
    setOpt(newOpt);
    props.updateOption(props.index, opt);
  }

  const handleJumpIndexChange = e => {
    let newOpt = opt;
    newOpt['cascdeIndex'] = e.target.value;
    setOpt(newOpt);
    props.updateOption(props.index, opt);
  }

  return (
    
    <Grid container direction="row" >
      <Grid item>
        <RadioButtonUncheckedIcon className={classes.optionIcon} />
      </Grid>
      <Grid item>
        <TextField 
        label={"option "+(props.index+1)}
        value={opt.name}
        onChange={handleNameChange}
        className={classes.optionInput}
        InputLabelProps={{
          shrink: true,
        }}
        />
      </Grid>
      <Grid item>
        <TextField
          label="jump to"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
          onChange={handleJumpIndexChange}
          className={classes.cascadeInput}
          value={opt.cascadeIndex}
        />
      </Grid>
      <Grid item>
        <IconButton onClick={deleteItself} className={classes.optionDelete}>
          <ClearIcon  />
        </IconButton>
      </Grid>
    </Grid>
  );
}

//multi choices
export default function McQuestion (props) {

  const classes = useStyles();
  const [options, setOptions] = useState(props.qInfo.options);

  useEffect(() => {
    setOptions(props.qInfo.options);
  })

  const updateOptions = (index, opt) =>{
    // let newOptions = options;
    // newOptions[index] = opt;
    // setOptions(newOptions);
    props.updateOpt(options);
  }

  const addOptions = e => {
    setOptions([...options, {name:'', cascadeIndex: ''}]);
    props.updateOpt(options);
    console.log(options);
  }

  const deleteOptions = (index, e) => {
    let newOptions = options.slice();
    newOptions.splice(index, 1);
    setOptions(()=>newOptions);
    props.updateOpt(options);
  }

  console.log(options);
  return (
    <React.Fragment>
      {
        options.map(
          (opt, index) => {
            console.log("in map "+index);
            return (
            <Option 
            key={index} 
            index={index} 
            option={opt} 
            updateOption={updateOptions}
            deleteOption={deleteOptions}
            />
        )})
      }
      <Button color="primary" onClick={addOptions} className={classes.optionAddButton}>
        Add Option
      </Button>
    </React.Fragment>
  );
}
