import React, { useState } from 'react';
import { Paper, makeStyles, Typography, Grid, RadioGroup, Radio, FormControl, FormControlLabel } from '@material-ui/core';


import styles from "../styles/QuestionStyle";

const useStyles = makeStyles(styles);

export default function Question(props) {

  const classes = useStyles();
  const [value, setValue] = useState(2);
  
  // console.log(value);
  // console.log(props.qInfo);
  

  const handleRadioChange = (e) => {
    setValue(e.target.value);
    props.update(props.index, value);
  }

  const displayMainArea = () => {
    if (props.qInfo.type==="mc") {
      return (
        <FormControl component="fieldset" >
        <RadioGroup onChange={handleRadioChange} value={value.toString()}>
          {
            props.qInfo.options.map((opt, index) => (
              <FormControlLabel 
              value={opt.optId.toString()} 
              control={<Radio />} 
              label={opt.content}
              key={index} />
            ))
          }
        </RadioGroup>
        </FormControl>
      );
    }
  }

  return (
    <React.Fragment>
      <Paper>
        <Grid container direction="column" alignItems="flex-start" >
          <Grid item>
            <Typography> {props.qInfo.content} </Typography>
          </Grid>
          <Grid item>
            {displayMainArea()}
          </Grid>
        </Grid>
      </Paper>
    </React.Fragment>
  );

}
