import React, { useState, useEffect } from 'react';

import Question from "../components/Question";
import { Grid, Typography, Button, Paper } from '@material-ui/core';

export default function Survey(props) {

  const [cascade, setCascade] = useState([]); //记录被级联的问题id，在表里的不显示
  let questions = [];
  let answers = [];
  let title;
  let cas = [3, 7, 8, 10, 3, 6, 9];

  useEffect(() => {
    //fetch 获取 title
    //fetch 获取 questions
    //set state of cascade

    //test: setcascade
    setCascade([3, 7, 8, 10, 3, 6, 9]);
    console.log(cascade);
  }, []);

  //test
  questions = [
    {
      formId: 8,
      qstnId: 1,
      type: "mc",
      content: "question 1: multiple choices",
      options: [
        {
          optId: 1,
          content: "option 1",
          cascadeQstnId: 3
        },
        {
          optId: 1,
          content: "option 2",
          cascadeQstnId: 4
        }
      ]
    },
    {
      formId: 8,
      qstnId: 2,
      type: "realNum",
      content: "question 2: real number",
      options: []
    },
    {
      formId: 8,
      qstnId: 3,
      type: "scale",
      content: "question 3: scale",
      options: []
    },
    {
      formId: 8,
      qstnId: 4,
      type: "cb",
      content: "question 4: checkboxes",
      options: [
        {
          optId: 1,
          content: "always like this",
          cascadeQstnId: undefined
        },
        {
          optId: 1,
          content: "always like this",
          cascadeQstnId: undefined
        }
      ]
    }
  ];
  title = "SURVEY 1";
  console.log(questions);

  const isCascaded = qstnId => {
    for (let i=0; i<cascade.length; i++)
      if (cascade[i]===qstnId) return true;
    return false;
  }

  const updateOptAnswer = (index, optId, oldOptId) => {
    console.log(index);
    console.log(optId);
    console.log(oldOptId);
    answers[index] = optId; //最后需要赋值
    console.log(answers);
    //接着要判断是否更新cascade数组
    //旧选项如果有级联，则改变后将级联id加回级联状态表
    if (oldOptId) {
      let cId = questions[index].options[oldOptId-1].cascadeQstnId;
      if (cId) {
        let cList = cascade.slice();
        cList = cList.unshift(cId);
        setCascade(cList);
      }
    }
    //当前新选项如果有级联，则在级联状态表删除掉所有id
    let cId = questions[index].options[optId-1].cascadeQstnId;
    if (cId) {   
      let cList = cascade.slice();
      for (let i=0; i<cList.length; i++) {
        if (cList[i] == cId) {
          cList = cList.splice(i, 1);
          --i;
        }
      }
      setCascade(cList);
    }
  }

  const updateCascade = () => {}

  const submitResponse = () => {}

  return (
    <React.Fragment>
      <Grid container direction="column" alignItems="center" >
        <Grid item>
          <Paper>
            <Typography>
              {title}
            </Typography>
          </Paper>
        </Grid>
        {
          questions.map((q, index) => {
            if (isCascaded(q.qstnId)) return null;
            return (
              <Grid item key={index}>
                <Question 
                index={index}
                qInfo = {q}
                update = {updateOptAnswer} />
              </Grid>
            );
          })
        }
        <Grid item>
          <Button
          variant = "contained"
          color = "primary"
          onClick = {submitResponse} >
            Submit
          </Button>
        </Grid>
      </Grid>
    </React.Fragment>
  );

}
