import React from "react";
import { Button, Grid, Paper, Typography, Link } from "@material-ui/core";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import { useState } from "react";

import styles from "../styles/SignFormStyle";

const useStyles = makeStyles(styles);

function Login(props) {
  const classes = useStyles();
  const [error, setError] = useState({emailEmpty: false, pwdEmpty: false, emailError: false});

  const validateEmail = (e) => {
    if (e.target.value==="") {
      let {emailEmpty, ...others} = error;
      emailEmpty = true;
      console.log(emailEmpty);
      setError({emailEmpty, ...others});
      console.log(error);
    } else {
      const regex = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
      if (!regex.test(e.target.value)) {
        let {emailError, ...others} = error;
        console.log(others);
        emailError = true;
        setError({emailError, ...others});
        console.log(error);
      }
    }
  }

  const emailHelperTest = () => {
    if (error.emailEmpty) {
      return "email address is required"
    } 
    else if (error.emailError) {
      return "Please input correct email address"
    }
    else {
      return "";
    }
  }

  return (
    <Grid container justify="center" alignItems="center" style={{ minHeight: "100vh" }}>
      <Grid item sm={10} md={6} lg={3}>
        <Paper className={classes.paper} elevation={3}>

          <Grid
            container
            direction="column"
            justify="center"
            alignItems="center"
            spacing={1}
            className={classes.formHeader}
          >
            <Grid item>
              <AccountCircleIcon color="primary" style={{ fontSize: 48 }}/>
            </Grid>
            <Grid item>
              <Typography variant="h4">Sign in</Typography>
            </Grid>
            <Grid item>
              <Typography variant="body2">
                {"No account yet? "}
                <Link href="/signup" underline="always">
                  Sign up here
                </Link>
              </Typography>
            </Grid>
          </Grid>

          <form className={classes.form}>
            <TextField
              id="outlined-helperText"
              label="Email"
              helperText=""
              variant="outlined"
              margin="normal"
              size="medium"
              fullWidth
              required
              onBlur={validateEmail}
              error={error.emailEmpty||error.emailError}
            />
            <TextField
              id="outlined-helperText"
              label="Password"
              helperText=""
              variant="outlined"
              margin="normal"
              size="medium"
              fullWidth
              required
            />
            <Button variant="contained" color="primary" size="medium" fullWidth className={classes.button}>
              Sign In
            </Button>
          </form>

          <Typography variant="body2" align="center">
            <Link href="/" underline="always">
              Forgot password?
            </Link>
          </Typography>

        </Paper>
      </Grid>
    </Grid>
  );
}

export default Login;
