import React, { useState, useEffect } from "react";
import Grid from "@material-ui/core/Grid";
import { Typography, makeStyles, Toolbar, IconButton } from "@material-ui/core";
import UpdateIcon from '@material-ui/icons/Update';
import Button from "@material-ui/core/Button";
import { useCookies } from 'react-cookie';

import AppHeader from "../components/AppHeader";
import AppDrawer from "../components/AppDrawer";
import SurveyCard from "../components/SurveyCard";
import styles from "../styles/HomePageStyle";


const useStyles = makeStyles(styles);

// const fetchAllForms = () => {
//   let headers = new Headers();
//   headers.append("Content-Type", "application/json;charset=UTF-8");
//   const init = {
//     method: 'GET',
//     headers: headers
//   };
//   fetch("/api/forms", init)
//     .then(res => res.json())
//     .then(data => {console.log(data);forms=JSON.parse(JSON.stringify(data)).slice;});
// }


export default function Home(props) {

  const classes = useStyles();
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [forms, setForms] = useState([]);
  const [cookies, setCookie] = useCookies();

  //验证cookies以检查登陆状态
  if (cookies.mail==null) props.history.replace("/login");


  useEffect(() => {
    let headers = new Headers();
    headers.append("Content-Type", "application/json;charset=UTF-8");
    const init = {
      method: 'GET',
      headers: headers
    };
    fetch("/api/forms", init)
      .then(res => res.json())
      .then (data => JSON.parse(JSON.stringify(data)))
      .then (data => setForms(data));
    console.log(forms);
  }, [drawerOpen]) //drawerOpen是为了阻止无限循环，参考 https://medium.com/@andrewmyint/infinite-loop-inside-useeffect-react-hooks-6748de62871
  // const forms = fetchAllForms()
  // console.log(forms);

  //////////////
  const nameTest="Survey Demo X";
  const idTest="00000";
  const responseTest=8;
  const dateTest= new Date().toLocaleDateString();
  ///////////////////////////

  const handleDrawerOpen = (e) => {
    setDrawerOpen(true);
  }

  const handleDrawerClose = e => {
    setDrawerOpen(false);
  }

  const handleNewForm = e => {
    props.history.push("/edit/00001");
  }

  return (
    <React.Fragment>

      <AppHeader handleDrawerOpen={handleDrawerOpen} drawerOpen={drawerOpen} />
      <AppDrawer open={drawerOpen} handleDrawerClose={handleDrawerClose} />
      <Toolbar />

      
      <Grid container direction="row" justify="center" alignItems="flex-start">
        <Grid item sm={1} md={3}></Grid>
        <Grid item container md>
          <Grid item flex-grow={1} className={classes.listHead}>
            <Typography variant="subtitle2" className={classes.headFont}>Recent Forms</Typography>
          </Grid>
          <Grid item className={classes.leftHead} >
            <IconButton className={classes.updateIcon}>
              <UpdateIcon />
            </IconButton>
          </Grid>
          <Grid item>
            <Button 
            onClick={handleNewForm}
            className={classes.addButton} 
            variant="contained" 
            size="small" 
            color="primary">
              +  new form
            </Button>
          </Grid>
        </Grid>
        <Grid item sm={1} md={3}></Grid>
      </Grid>
      
     <Grid 
      container 
      direction="column" 
      alignItems="center" 
      
      className={classes.mainArea}
    >
      {
        forms instanceof Array?
        forms.map((form, index) => (
          <Grid item key={index}>
            <SurveyCard name={form.title} id={form.formId} resNum={0} date={form.date} />
          </Grid>
        )): null
      }     
    </Grid>
    </React.Fragment>
  );
}
