const styles = theme => ({
  appBar: {
    background: "transparent",
    boxShadow: "none",
  },
  surveyIcon: {
    fontSize: 40,
    marginRight: (0,6,0,6),
  },
  avatar: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    marginRight: theme.spacing(2),
  },
  name: {
    flexGrow: 1,
  },
  notifIcon: {
    margin: theme.spacing(4),
    color: theme.palette.primary.light,
    backgroundColor: 'transparent',
  }
});

export default styles;
