import React, { useState } from 'react';
import { Paper, makeStyles, Typography, Grid, RadioGroup, Radio, FormControl, FormControlLabel } from '@material-ui/core';


import styles from "../styles/QuestionStyle";

const useStyles = makeStyles(styles);

export default function Question(props) {

  const classes = useStyles();
  const [optId, setOptId] = useState(1);
  let oldOptId = null;

  console.log(props.qInfo);

  const handleRadioChange = (e) => {
    console.log(e.target.value);
    setOptId(e.target.value);
    
    //props.update(props.index, optId, oldOptId);
    oldOptId = optId;
    console.log(optId);
  }

  const displayMainArea = () => {
    if (props.qInfo.type==="mc") {
      return (
        <FormControl component="fieldset" >
        <RadioGroup onChange={handleRadioChange} value={optId}>
          {
            props.qInfo.options.slice().map((opt, index) => (
              <FormControlLabel 
              value={opt.optId} 
              control={<Radio />} 
              label={opt.content}
              key={index} />
            ))
          }
        </RadioGroup>
        </FormControl>
      );
    }
  }

  return (
    <React.Fragment>
      <Paper>
        <Grid container direction="column" alignItems="flex-start" >
          <Grid item>
            <Typography> {props.qInfo.content} </Typography>
          </Grid>
          <Grid item>
            {displayMainArea()}
          </Grid>
        </Grid>
      </Paper>
    </React.Fragment>
  );

}
