import React, { useState } from "react";
import { makeStyles, Grid, TextField } from "@material-ui/core";
import RadioButtonUncheckedIcon from '@material-ui/icons/RadioButtonUnchecked';


import styles from "../styles/QuestionStyle";


const useStyles = makeStyles(styles);


function Option(props) {

  const [opt, setOpt] = useState(props.option);

  const updateOptName = e => {
    setOpt({name: e.target.value, ...opt});
    props.updateOption();
  }

  const updateOptCascade = e => {
    setOpt({...opt, cascadeIndex: e.target.value});
    props.updateOption();
  }

  return (
    
    <Grid container direction="row" >
      <Grid item>
        <RadioButtonUncheckedIcon />
      </Grid>
      <Grid item>
        <TextField 
        label={"option "+(props.index+1)}
        onBlur={updateOptName}
        />
      </Grid>
      <Grid item>
        <TextField
          label="jump to"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
          onBlur={updateOptCascade}
        />
      </Grid>
    </Grid>
  );
}

//multi choices
export default function McQuestion (props) {

  const classes = useStyles();
  const [options, setOptions] = useState(props.qInfo.options);

  const updateOptions = (index, opt) =>{
    console.log(opt);
    console.log(index);
    let newOptions = options;
    newOptions[index] = opt;
    setOptions(newOptions);
    props.updateOpt();
  }

  return (
    <React.Fragment>
      {
        options.map(
          (opt, index) => (
            <Option 
            key={index} 
            index={index} 
            option={opt} 
            updateOption={updateOptions}
            />
        ))
      }
    </React.Fragment>
  );
}
