import React, { useState } from 'react';
import { Paper, makeStyles, Typography, Grid, RadioGroup, Radio } from '@material-ui/core';


import styles from "../styles/QuestionStyle";

const useStyles = makeStyles(styles);

export default function Question(props) {

  const classes = useStyles();
  const [value, setValue] = useState("");

  console.log(props.qInfo);

  const handleRadioChange = (e) => {
    setValue(e.target.value);
    console.log(e.target.value);
  }

  const displayMainArea = () => {
    if (props.qInfo.type==="mc") {
      return (
        <RadioGroup onChange={handleRadioChange} value={value}>
          {
            props.qInfo.options.map((opt, index) => (
              <Radio value={opt.content} label={opt.content} key={index} />
            ))
          }
        </RadioGroup>
      );
    }
  }

  return (
    <React.Fragment>
      <Paper>
        <Grid containter direction="column" alignItems="flex-start" >
          <Grid item>
            <Typography> {props.qInfo.content} </Typography>
          </Grid>
          <Grid item>
            {displayMainArea()}
          </Grid>
        </Grid>
      </Paper>
    </React.Fragment>
  );

}
