import React from "react";
import { makeStyles } from "@material-ui/core";


import styles from "../styles/QuestionStyle";


const useStyles = makeStyles(styles);



//multi choices
function mcQuestions (content, options) {
  return (
    <h1>mcQuestions</h1>
  )
}

function cbQuestions (content, options) {
  return (
    <h1>cbQuestions</h1>
  )
}

function textQuestions (content) {
  return (
    <h1>textQuestions</h1>
  )
}

function numQuestions (content) {
  return (
    <h1>numQuestions</h1>
  )
}

function scaleQuestions (content) {
  return (
    <h1>scaleQuestions</h1>
  )
}

export default function Question(props) {

  const classes = useStyles();

  console.log(props.qInfo);

  let question;

  if (props.qInfo.type==='mcq')         
    question = mcQuestions(props.qInfo.content, props.qInfo.options);
  else if (props.qInfo.type==='cb')     
    question = cbQuestions(props.qInfo.content, props.qInfo.options);
  else if (props.qInfo.type==='text')   
    question = textQuestions(props.qInfo.content);
  else if (props.qInfo.type==='num')    
    question = numQuestions(props.qInfo.content);
  else if (props.qInfo.type==='scale')  
    question = scaleQuestions(props.qInfo.content);

  console.log(question);

  return (
    <React.Fragment>

      { question }
        
    </React.Fragment>
  )

}
