import React, { useState } from "react";
import { makeStyles, Paper, Grid } from "@material-ui/core";


import styles from "../styles/QuestionStyle";
import McQuestion from "./McQuestion";
import CbQuestion from "./CbQuestion";
import TextQuestion from "./TextQuestion";
import NumQuestion from "./NumQuestion";
import ScaleQuestion from "./ScaleQuestion";



const useStyles = makeStyles(styles);


export default function Question(props) {

  const classes = useStyles();
  const [type, setType] = useState("mc");
  const [selectorOpen, setSelectorOpen] = useState(false);

  console.log(props.key);


  let question;
  if (props.qInfo.type==="mc") question = <McQuestion qInfo={props.qInfo} />;
  else if (props.qInfo.type==="cb")  question = <CbQuestion qInfo={props.qInfo} />;
  else if (props.qInfo.type==="text")  question = <TextQuestion qInfo={props.qInfo} />;
  else if (props.qInfo.type==="num")   question = <NumQuestion qInfo={props.qInfo} />;
  else if (props.qInfo.type==="scale") question = <ScaleQuestion qInfo={props.qInfo} />;

  return (
    <React.Fragment>
      <Paper className={classes.questionPaper}>
        <Grid container direction="row" alignItems="flex-start">
          <Grid item className={classes.mainArea}>
            <McQuestion qInfo={props.qInfo} />
          </Grid>
          <Grid item >
            
          </Grid>
        </Grid>
        
      </Paper>
      
        
    </React.Fragment>
  )

}
